import React, { useState, useEffect, useRef } from 'react';
import { TrendingUp, TrendingDown, Minus, RefreshCw, Bell, Search, Activity } from 'lucide-react';
import { FALLBACK_STOCKS } from './stockFallback';

// API Configuration
const API_URL = 'http://localhost:5000/api';
const WS_URL = 'ws://localhost:5000';

function App() {
  const [stocks, setStocks] = useState([]);
  const [stockOptions, setStockOptions] = useState([]); // list from backend config
  const [selectedStock, setSelectedStock] = useState(null);
  const [sentimentData, setSentimentData] = useState({});
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [newsItems, setNewsItems] = useState([]);
  const [wsConnected, setWsConnected] = useState(false);
  const ws = useRef(null);

  // Connect to WebSocket
  useEffect(() => {
    connectWebSocket();
    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, []);

  const connectWebSocket = () => {
    ws.current = new WebSocket(WS_URL);
    
    ws.current.onopen = () => {
      console.log('WebSocket Connected');
      setWsConnected(true);
    };
    
    ws.current.onmessage = (event) => {
      const message = JSON.parse(event.data);
      handleWebSocketMessage(message);
    };
    
    ws.current.onerror = (error) => {
      console.error('WebSocket Error:', error);
      setWsConnected(false);
    };
    
    ws.current.onclose = () => {
      console.log('WebSocket Disconnected');
      setWsConnected(false);
      // Reconnect after 5 seconds
      setTimeout(connectWebSocket, 5000);
    };
  };

  const handleWebSocketMessage = (message) => {
    switch (message.type) {
      case 'initial_data':
        setStocks(message.data);
        setLoading(false);
        break;
      case 'stock_update':
        updateStockData(message.data);
        break;
      case 'sentiment_update':
        updateSentimentData(message.data);
        break;
      default:
        break;
    }
  };

  const updateStockData = (data) => {
    data.forEach(item => {
      setStocks(prev => {
        const index = prev.findIndex(s => s.symbol === item.stock.symbol);
        if (index !== -1) {
          const newStocks = [...prev];
          newStocks[index] = item.stock;
          return newStocks;
        }
        return prev;
      });
      
      if (item.sentiment) {
        setSentimentData(prev => ({
          ...prev,
          [item.stock.symbol]: item.sentiment
        }));
      }
    });
  };

  const updateSentimentData = (data) => {
    data.forEach(item => {
      setSentimentData(prev => ({
        ...prev,
        [item.symbol]: item
      }));
    });
  };

  // Fetch initial data
  useEffect(() => {
    fetchStockOptions();
    fetchStocks();
  }, []);

  // Fetch configured stock list for dropdown (fallback to local copy on error)
  const fetchStockOptions = async () => {
    try {
      // prefer a stable config endpoint that won't collide with param routes
      const response = await fetch(`${API_URL}/stocks/config/list`, { timeout: 5000 });
      const data = await response.json();
      if (data.success && Array.isArray(data.data)) {
        setStockOptions(data.data);
        return;
      }
    } catch (err) {
      console.warn('Could not fetch stock options from backend, using fallback', err.message);
    }

    // Fallback
    setStockOptions(FALLBACK_STOCKS);
  };

  const fetchStocks = async () => {
    try {
      const response = await fetch(`${API_URL}/stocks`);
      const data = await response.json();
      if (data.success) {
        setStocks(data.data);
        // Fetch sentiment for each stock
        data.data.forEach(stock => fetchSentiment(stock.symbol));
      }
    } catch (error) {
      console.error('Error fetching stocks:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchSentiment = async (symbol) => {
    try {
      const response = await fetch(`${API_URL}/sentiment/${symbol}`);
      const data = await response.json();
      if (data.success && data.data) {
        setSentimentData(prev => ({
          ...prev,
          [symbol]: data.data
        }));
      }
    } catch (error) {
      console.error(`Error fetching sentiment for ${symbol}:`, error);
    }
  };

  const fetchNews = async (symbol) => {
    try {
      const response = await fetch(`${API_URL}/sentiment/${symbol}/news`);
      const data = await response.json();
      if (data.success) {
        setNewsItems(data.data);
      }
    } catch (error) {
      console.error('Error fetching news:', error);
    }
  };

  const handleStockSelect = (stock) => {
    setSelectedStock(stock);
    fetchNews(stock.symbol);
  };

  const getSentimentColor = (score) => {
    if (score > 0.2) return 'text-green-600';
    if (score < -0.2) return 'text-red-600';
    return 'text-gray-600';
  };

  const getSentimentBg = (score) => {
    if (score > 0.2) return 'bg-green-50 border-green-200';
    if (score < -0.2) return 'bg-red-50 border-red-200';
    return 'bg-gray-50 border-gray-200';
  };

  const getSentimentIcon = (score) => {
    if (score > 0.2) return <TrendingUp className="w-4 h-4" />;
    if (score < -0.2) return <TrendingDown className="w-4 h-4" />;
    return <Minus className="w-4 h-4" />;
  };

  const filteredStocks = stocks.filter(stock =>
    stock.symbol.toLowerCase().includes(searchQuery.toLowerCase()) ||
    stock.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="w-12 h-12 animate-spin text-indigo-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading Indian Stock Market Data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Activity className="w-8 h-8 text-indigo-600" />
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Indian Stock Sentiment Analyzer</h1>
                <p className="text-sm text-gray-600">Real-time NSE/BSE sentiment tracking</p>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <div className={`flex items-center gap-2 px-3 py-1 rounded-full ${wsConnected ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                <div className={`w-2 h-2 rounded-full ${wsConnected ? 'bg-green-500' : 'bg-red-500'}`} />
                <span className="text-xs font-medium">{wsConnected ? 'Live' : 'Disconnected'}</span>
              </div>
              <button onClick={fetchStocks} className="p-2 hover:bg-gray-100 rounded-lg transition">
                <RefreshCw className="w-5 h-5 text-gray-600" />
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Stock List */}
          <div className="lg:col-span-1 space-y-4">
            <div className="bg-white rounded-lg shadow-sm p-4">
              <div className="relative mb-4">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search stocks..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                />
              </div>
              
              <div className="space-y-2 max-h-[600px] overflow-y-auto">
                {stockOptions.map(opt => {
                  // find detailed data if available
                  const stock = stocks.find(s => s.symbol === opt.symbol) || null;
                  const sentiment = sentimentData[opt.symbol];
                  const isSelected = selectedStock?.symbol === opt.symbol;

                  return (
                    <button
                      key={opt.symbol}
                      onClick={() => handleStockSelect(opt)}
                      className={`w-full text-left p-3 rounded-lg border transition ${
                        isSelected 
                          ? 'bg-indigo-50 border-indigo-300' 
                          : 'bg-white border-gray-200 hover:border-indigo-200 hover:bg-gray-50'
                      }`}
                    >
                      <div className="flex items-start justify-between mb-2">
                        <div>
                          <div className="font-semibold text-gray-900">{opt.symbol.replace('.NS', '')}</div>
                          <div className="text-xs text-gray-600 truncate max-w-[150px]">{opt.name}</div>
                        </div>
                        <div className="text-right">
                          <div className="font-semibold text-gray-900">{stock && stock.currentPrice ? `₹${stock.currentPrice.toFixed(2)}` : '—'}</div>
                          <div className={`text-xs ${stock && stock.change >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                            {stock && typeof stock.changePercent === 'number' ? `${stock.changePercent.toFixed(2)}%` : ''}
                          </div>
                        </div>
                      </div>
                      
                      {sentiment && (
                        <div className={`flex items-center justify-between text-xs p-2 rounded border ${getSentimentBg(sentiment.avgSentiment)}`}>
                          <span className="font-medium text-gray-700">Sentiment</span>
                          <div className={`flex items-center gap-1 ${getSentimentColor(sentiment.avgSentiment)}`}>
                            {getSentimentIcon(sentiment.avgSentiment)}
                            <span className="font-semibold">{sentiment.avgSentiment?.toFixed(2)}</span>
                          </div>
                        </div>
                      )}
                    </button>
                  );
                })}
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-2 space-y-4">
            {selectedStock ? (
              <>
                {/* Stock Details */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div>
                      <h2 className="text-2xl font-bold text-gray-900">{selectedStock.name}</h2>
                      <p className="text-gray-600">{selectedStock.symbol}</p>
                    </div>
                    <div className="text-right">
                      <div className="text-3xl font-bold text-gray-900">₹{selectedStock.currentPrice?.toFixed(2)}</div>
                      <div className={`text-lg ${selectedStock.change >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                        {selectedStock.change >= 0 ? '+' : ''}{selectedStock.change?.toFixed(2)} ({selectedStock.changePercent?.toFixed(2)}%)
                      </div>
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-4 pt-4 border-t border-gray-200">
                    <div>
                      <div className="text-sm text-gray-600">Volume</div>
                      <div className="text-lg font-semibold text-gray-900">
                        {(selectedStock.volume / 1000000).toFixed(2)}M
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Previous Close</div>
                      <div className="text-lg font-semibold text-gray-900">₹{selectedStock.previousClose?.toFixed(2)}</div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Exchange</div>
                      <div className="text-lg font-semibold text-gray-900">{selectedStock.exchange}</div>
                    </div>
                  </div>
                </div>

                {/* Sentiment Analysis */}
                {sentimentData[selectedStock.symbol] && (
                  <div className="bg-white rounded-lg shadow-sm p-6">
                    <h3 className="text-lg font-bold text-gray-900 mb-4">Sentiment Analysis (24h)</h3>
                    
                    <div className="grid grid-cols-2 gap-4 mb-4">
                      <div className={`p-4 rounded-lg border ${getSentimentBg(sentimentData[selectedStock.symbol].avgSentiment)}`}>
                        <div className="text-sm text-gray-600 mb-1">Average Sentiment</div>
                        <div className={`text-2xl font-bold ${getSentimentColor(sentimentData[selectedStock.symbol].avgSentiment)}`}>
                          {sentimentData[selectedStock.symbol].avgSentiment?.toFixed(3)}
                        </div>
                        <div className="text-xs text-gray-600 mt-1">
                          {sentimentData[selectedStock.symbol].sentimentTrend?.toUpperCase()}
                        </div>
                      </div>
                      
                      <div className="p-4 rounded-lg border border-gray-200 bg-gray-50">
                        <div className="text-sm text-gray-600 mb-1">Total Mentions</div>
                        <div className="text-2xl font-bold text-gray-900">
                          {sentimentData[selectedStock.symbol].totalMentions}
                        </div>
                        <div className="text-xs text-gray-600 mt-1">News articles analyzed</div>
                      </div>
                    </div>

                    <div className="grid grid-cols-3 gap-3">
                      <div className="p-3 rounded-lg bg-green-50 border border-green-200">
                        <div className="text-xs text-green-700 font-medium">Positive</div>
                        <div className="text-xl font-bold text-green-600">
                          {sentimentData[selectedStock.symbol].positiveCount}
                        </div>
                      </div>
                      <div className="p-3 rounded-lg bg-gray-50 border border-gray-200">
                        <div className="text-xs text-gray-700 font-medium">Neutral</div>
                        <div className="text-xl font-bold text-gray-600">
                          {sentimentData[selectedStock.symbol].neutralCount}
                        </div>
                      </div>
                      <div className="p-3 rounded-lg bg-red-50 border border-red-200">
                        <div className="text-xs text-red-700 font-medium">Negative</div>
                        <div className="text-xl font-bold text-red-600">
                          {sentimentData[selectedStock.symbol].negativeCount}
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {/* News Feed */}
                <div className="bg-white rounded-lg shadow-sm p-6">
                  <h3 className="text-lg font-bold text-gray-900 mb-4">Recent News & Analysis</h3>
                  
                  {newsItems.length > 0 ? (
                    <div className="space-y-3 max-h-[400px] overflow-y-auto">
                      {newsItems.map((news, index) => (
                        <div key={index} className={`p-3 rounded-lg border ${getSentimentBg(news.sentimentScore)}`}>
                          <div className="flex items-start justify-between gap-3">
                            <div className="flex-1">
                              <h4 className="font-semibold text-gray-900 text-sm mb-1">{news.headline}</h4>
                              {news.content && (
                                <p className="text-xs text-gray-600 line-clamp-2">{news.content}</p>
                              )}
                              <div className="flex items-center gap-3 mt-2 text-xs text-gray-500">
                                <span>{news.source}</span>
                                <span>•</span>
                                <span>{new Date(news.timestamp).toLocaleString()}</span>
                              </div>
                            </div>
                            <div className={`flex items-center gap-1 ${getSentimentColor(news.sentimentScore)}`}>
                              {getSentimentIcon(news.sentimentScore)}
                              <span className="text-xs font-semibold">{news.sentimentScore?.toFixed(2)}</span>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8 text-gray-500">
                      <Bell className="w-12 h-12 mx-auto mb-2 text-gray-400" />
                      <p>No recent news available</p>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <div className="bg-white rounded-lg shadow-sm p-12 text-center">
                <Activity className="w-16 h-16 mx-auto mb-4 text-gray-400" />
                <h3 className="text-xl font-semibold text-gray-900 mb-2">Select a Stock</h3>
                <p className="text-gray-600">Choose a stock from the list to view detailed sentiment analysis and news</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
